import Link from "next/link";
import { useState, useEffect } from "react";
import Router from "next/router";
import { getCookie, isAuth } from "../../actions/auth";
import { list, removeBlog } from "../../actions/blog";
import moment from "moment";
import { Button } from '@material-ui/core';
import { Card, CardTitle, CardText, CardImg, CardImgOverlay } from 'reactstrap';


const ReadBlogs = ({ username }) => {
  const [blogs, setBlogs] = useState([]);
  const [message, setMessage] = useState("");
  const token = getCookie("token");

  useEffect(() => {
    loadBlogs();
  }, []);

  const loadBlogs = () => {
    list(username).then(data => {
      if (data.error) {
        console.log(data.error);
      } else {
        setBlogs(data);
      }
    });
  };

  const deleteConfirm = slug => {
    let answer = window.confirm("Are you sure you want to delete your blog");
    if (answer) {
      deleteBlog(slug);
    }
  };

  const deleteBlog = slug => {
    removeBlog(slug, token).then(data => {
      if (data.error) {
        console.log(data.error);
      } else {
        setMessage(data.message);
        loadBlogs();
      }
    });
  };

  const showUpdateButton = blog => {
    if (isAuth() && isAuth().role === 0) {
      return (
        <Link href={`/user/crud/${blog.slug}`}>
          <a className="btn btn-sm btn-warning">Update</a>
        </Link>
      );
    } else if (isAuth() && isAuth().role === 1) {
      return (
        <Link href={`/admin/crud/${blog.slug}`}>
          <a className="ml-5 btn btn-sm btn-warning">Update</a>
        </Link>
      );
    }
  };

  const showAllBlogs = () => {
    return blogs.slice(0).reverse().map((blog, i) => {
      return (
        <div key={i} className="mt-5 pb-5">
            <Card inverse className="mt-2">
      <CardImg width="100%" height="130px" src={`${API}/blog/photo/${blog.slug}`} alt={blog.title} />
        <CardImgOverlay>
          <CardTitle><Link href={`/blogs/${blog.slug}`}>
            {blog.title}

          </Link></CardTitle>

          <CardText>
            <small className="text-muted">{moment(blog.updatedAt).fromNow()} </small>
          </CardText>
          <button
            className="btn btn-sm btn-danger mr-3"
            onClick={() => deleteConfirm(blog.slug)}
          >
            Delete
          </button>
          {showUpdateButton(blog)}
        </CardImgOverlay>
      </Card>
          {/* <Link href={`/user/crud/${blog.slug}`}>
            <a className="">{blog.title}</a>
          </Link>
          <p className="mark">
            Written by {blog.postedBy.name} | Published on{" "}
            {moment(blog.updatedAt).fromNow()}
          </p> */}

        </div>
      );
    });
  };

  return (
    <React.Fragment>
      <div className="row">
        <div className="col-md-12">
          {message && <div className="alert alert-warning">{message}</div>}
          {showAllBlogs()}
        </div>
      </div>
    </React.Fragment>
  );
};

export default ReadBlogs;
