import Layout from "../../components/Layout";
import Link from "next/link";
import Head from "next/head";
import { withRouter } from "next/router";
import { useState } from "react";
import { listBlogsWithCategoriesAndTags } from "../../actions/blog";
import { API, DOMAIN, APP_NAME } from "../../config";
import BlogCard from "../../components/blog/BlogCard";
import Search from "../../components/blog/Search";


const Blogs = ({
  blogs,
  categories,
  tags,
  totalBlogs,
  blogSkip,
  blogsLimit,
  router
}) => {
  const head = () => (
    <Head>
      <title>Travel Blogs | {APP_NAME}</title>
      <meta name="description" content="Travelling Blogs World Wide" />

      <Link rel="canonical" href={`${DOMAIN}${router.pathname}`} />
      <meta
        property="og:title"
        content={`Best travel blogging site for suggestions | ${APP_NAME}`}
      />
      <meta
        property="og:description"
        content="Best Travel Blogs online and flight booking systems"
      />
      <meta property="og:type" content="website" />
      <meta property="og:url" content={`${DOMAIN}${router.pathname}`} />
      <meta property="og:site_name" content={`${APP_NAME}`} />
      {/* <meta property="og:image" content="/static/images/" />
      <meta property="og:image:secure_url" content="/static/images/" />
      <meta property="og:image:type" content={`${APP_NAME}`} />
      <meta property="fb:app_id" content={`${APP_NAME}`} /> */}
    </Head>
  );

  const [limit, setLimit] = useState(blogsLimit);
  const [skip, setSkip] = useState(0);
  const [size, setSize] = useState(totalBlogs);
  const [loadedBlogs, setLoadedBlogs] = useState([]);

  const showAllBlogsCards = () => {
    return blogs.map((blog, i) => (
      <div className="col-md-4 " key={i}>
        <article>
          <RecentCard blog={blog} />
        </article>
      </div>
    ));
  };

  const loadMore = () => {
    let toSkip = skip + limit;
    listBlogsWithCategoriesAndTags(toSkip, limit).then(data => {
      if (data.error) {
        console.log(data.error);
      } else {
        setLoadedBlogs([...loadedBlogs, ...data.blogs]);
        setSize(data.size);
        setSkip(toSkip);
      }
    });
  };

  const loadMoreButton = () => {
    return (
      size > 0 &&
      size >= limit && (

        <button onClick={loadMore} className="btn btn-outline-primary btn-lg" style={{width:"100%"}}>
          Load More
        </button>

      )
    );
  };

  const showAllCategories = () => {
    return categories.map((c, i) => (
      <ul style={{listStyle:'none'}} key={i}>
        <Link href={`/categories/${c.slug}`}>
          <li id="list-group-blogs">
            <a id="list-a">{c.name}</a>
          </li>
        </Link>
      </ul>
    ));
  };
  const showAllTags = () => {
    return tags.map((t, i) => (

      <ul style={{listStyle:'none'}} key={i} >
        <Link href={`/tags/${t.slug}`}>
          <li  id="list-group-blogs">
            <a id="list-a">{t.name}</a>
          </li>
        </Link>
      </ul>
    ));
  };

  const showAllBlogs = () => {
    return blogs.map((blog, i) => (
      <article key={i}>
        <BlogCard blog={blog} />
      </article>
    ));
  };



  const showLoadedBlogs = () => {
    return loadedBlogs.map((blog, i) => (
      <article key={i}>
        <BlogCard blog={blog} />
      </article>
    ));
  };

  return (
    <React.Fragment>
      {/* {head()} */}

      <Layout>
        <main>
          <div className="container-fluid" id="blog-hero">
            <div className="container pt-5" style={{}}>
              <h1 className="display-4 pt-5 font-weight-bold text-center">
                Travel Blog
              </h1>
            </div>
          </div>
          <div className="container-fluid mt-4" style={{position:'relative'}}>
            <div className="row">
              <div className="col-md-8 mr-3 "  >
                {showAllBlogs()}
                <div className="container-fluid" >
                  {showLoadedBlogs()}
                </div>
                <div className="text-center pt-5 pb-5">{loadMoreButton()}</div>
                {showAllBlogsCards()}
              </div>

              <div className="col-md-3 pt-3 ml-3" id="blogs-right"  >
                <Search />
                <h2 className='mb-4 text-bold'>Categories</h2>
                {showAllCategories()}
                <br />
                <h2 className='mb-4 text-bold'>Tags</h2>
                {showAllTags()}
              </div>
            </div>
          </div>
        </main>
      </Layout>
    </React.Fragment>
  );
};

Blogs.getInitialProps = () => {
  let skip = 0;
  let limit = 3;

  return listBlogsWithCategoriesAndTags(skip, limit).then(data => {
    if (data.error) {
      console.log(data.error);
    } else {
      return {
        blogs: data.blogs,
        categories: data.categories,
        tags: data.tags,
        totalBlogs: data.size,
        blogsLimit: limit,
        blogSkip: skip
      };
    }
  });
};

export default withRouter(Blogs); //getInitialProps
