import React from "react";

import Link from "next/link";

const Footer = props => {
  return (
    // <div className="mt-5 pt-5 pb-5 footer">
    <div className="container-fluid text-center footer mt-5 pt-5 ">
      <div className="footer-menu">
        <ul>
          <li>
            <h4 className="ml-2 mr-2 text-white-50">Contact</h4>
          </li>
          <li>
            <h4 className="ml-2 mr-2 text-white-50">Terms</h4>
          </li>
          <li>
            <h4 className="ml-2 mr-2 text-white-50">Privacy</h4>
          </li>
          <li>
            <h4 className="ml-2 mr-2 text-white-50">About</h4>
          </li>
        </ul>
      </div>

      <div className="footer-social-icons">
        <p className=" ml-2 mr-2">
          <small className="text-white-50">Follow Us</small>
        </p>

        <ul className="mt-3">
          <li>
            <a href="#" className=" ml-2 mr-2">
              <i className="fa fa-facebook-square mr-1"></i>
            </a>
          </li>
          <li>
            <a href="#" className=" ml-2 mr-2">
              <i className="fa fa-twitter-square mr-1"></i>
            </a>
          </li>
          <li>
            <a href="#" className=" ml-2 mr-2">
              <i className="fa fa-linkedin-square"></i>
            </a>
          </li>
          <li>
            <a href="#" className=" ml-2 mr-2">
              <i className="fa fa-google-plus-square"></i>
            </a>
          </li>
        </ul>
      </div>
      <hr />
      <div className="row mt-3">
        <div className="col  ">
          <p className="">
            <small className="text-white-50">
              Â© 2020. Travel Blog All Rights Reserved.
            </small>
          </p>
        </div>
      </div>
      {/* </div> */}
      <style jsx>{`
        h4{
          font-size:2vh;
        }
        .footer {
          width: 100%
          height: auto;
          background: #2d73c0;
          color: white;
          padding: 20px 0px;
          align-items: center;
          justify-content: center;
        }
        .footer-social-icons{
          width:100%;
          height:auto;
          margin: auto;
        }
        .footer-social-icons ul{
          margin:0 px;
          padding: 0px;
          text-align: center;
        }
        .footer-social-icons ul li{
          display: inline-block;
          width: 50px;
          height: 50px;
          margin: 0px 10px;
          border-radius: 100%;
        } 
        .footer-menu{
          width:100%;
          height:auto;
          margin: auto;
        }
        .footer-menu ul{
          margin:0 px;
          padding: 0px;
          text-align: center;
        }
        .footer-menu ul li{
          display: inline-block;
          width: 50px;
          height: 50px;
          margin: 0px 10px;
          border-radius: 100%;
        }
      `}</style>
    </div>
  );
};

export default Footer;
