const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
}));


const Header = props => {

  const classes = useStyles();
  return (
    <div className={classes.root}>
    <AppBar position="static">
      <Toolbar>
        <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
          <MenuIcon />
        </IconButton>
        <Typography variant="h6" className={classes.title}>
          News
        </Typography>
    <Button color="inherit">
    <Link href="/blogs">
      <a href="/blogs">
        <a>Blogs</a>
      </a>
    </Link>
  </Button>

  {!isAuth() && (
    <Button color="inherit">
      <Link href="/signin">
        <a href="/signin">
          <a>Signin</a>
        </a>
      </Link>
    </Button>
  )}

  {!isAuth() && (
    <Button color="inherit">
      <Link href="/signup">
        <a href="/signup">
          <a>Signup</a>
        </a>
      </Link>
    </Button>
  )}

  {isAuth() && isAuth().role !== 1 && (
    <Button color="inherit">
      <Link href="/user">
        <a>Dashboard</a>
      </Link>
    </Button>
  )}

  {isAuth() && isAuth().role === 1 && (
    <Button color="inherit">
      <Link href="/admin">
        <a>Dashboard</a>
      </Link>
    </Button>
  )}

  {isAuth() && (
    <Button color="inherit">
      <Link
        style={{ cursor: "pointer" }}
        onClick={() => signout(() => Router.replace(`/signin`))}
      >
        <a>Signout</a>
      </Link>
    </Button>
  )}

  {isAuth() && (
    <Button color="inherit">
      <Link href="/user/crud/blog">
        <a href="/user/crud/blog">
          <a>Create</a>
        </a>
      </Link>
    </Button>
  )}

  {!isAuth() && (
    <Button color="inherit">
      <Link href="/signin">
        <a href="/signin">
          <a>Create</a>
        </a>
      </Link>
    </Button>
  )}

</Toolbar>
      </AppBar>
    </div>
  );
};

export default Header;
