import Link from "next/link";
import moment from "moment";
import { API } from "../config";
import ReactImageFallback from "react-image-fallback";
import { makeStyles } from '@material-ui/core/styles';
import renderHTML from "react-render-html";
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';

import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import { red } from '@material-ui/core/colors';

import ShareIcon from '@material-ui/icons/Share';

const useStyles = makeStyles(theme => ({
  root: {
    maxWidth: 345,
  },
  media: {
    height: 0,
    paddingTop: '56.25%', // 16:9
  },
  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(180deg)',
  },
  avatar: {
    backgroundColor: red[500],
  },
}));



const RecentCard = ({ blog }) => {
  const classes = useStyles();


  return (
    // <div className="card mb-3" style={{ height: "300px", width: "100%" }}>
    //   <section style={{ height: "200px", width: "100%" }}>
    //     <Link href={`/blogs/${blog.slug}`}>
    //       <a>
    //         <ReactImageFallback
    //           className="img img-fluid"
    //           style={{ height: "200px", width: "100%" }}
    //           src={`${API}/blog/photo/${blog.slug}`}
    //           fallbackImage="/static/default.svg"
    //           alt={blog.title}
    //         />
    //       </a>
    //     </Link>
    //   </section>

    //   <div
    //     className="card-body"
    //     style={{ height: "100px", overflow: "hidden" }}
    //   >
    //     <section>
    //       <Link href={`/blogs/${blog.slug}`}>
    //         <a>
    //           <h5 className="card-title">{blog.title}</h5>
    //         </a>
    //       </Link>
    //       <div className="card-text">
    //         Posted {moment(blog.updatedAt).fromNow()} by
    //         <Link href={`/profile/${blog.postedBy.username}`}>
    //           <a className="float-right">{blog.postedBy.username}</a>
    //         </Link>
    //       </div>
    //     </section>
    //   </div>
    // </div>
<Card className={classes.root}>
      <CardHeader
        avatar={
          <Avatar  className={classes.avatar}>
              <ReactImageFallback
          className="img img-fluid rounded-circle mr-4"
          style={{ height: "50px" , width:'50px'}}
          fallbackImage="/static/default.svg"
          src={`${API}/user/photo/${blog.postedBy.username}`}
          alt={blog.title}
        />
          </Avatar>
        }

        title={blog.title}
        subheader={moment(blog.updatedAt).fromNow()}
      />
      <CardMedia
        className={classes.media}
      >
           <Link href={`/blogs/${blog.slug}`}>
          <ReactImageFallback
            className="img img-fluid card-img-top rounded-0"
            style={{ height: "180px", width: "100%" }}
            fallbackImage="/static/default.svg"
            src={`${API}/blog/photo/${blog.slug}`}
            alt={blog.title}
          />
        </Link>
        </CardMedia>
      <CardContent>
        <Typography variant="body2" color="textSecondary" component="p">
        {renderHTML(blog.excerpt)}
        </Typography>
      </CardContent>
      <CardActions disableSpacing>

        <IconButton aria-label="share">
          <ShareIcon />
        </IconButton>

      </CardActions>

    </Card>

  );
};

export default RecentCard;
