import Layout from "../../components/Layout";
import Link from "next/link";
import Head from "next/head";
import { withRouter } from "next/router";
import { useState } from "react";
import { listBlogsWithCategoriesAndTags } from "../../actions/blog";
import { API, DOMAIN, APP_NAME } from "../../config";

import BlogCard from "../../components/blog/BlogCard";

const Blogs = ({ blogs, categories, tags, size, router }) => {
  const head = () => {
    return (
      <Head>
        <title>Travel Blogs | {APP_NAME}</title>
        <meta name="description" content="Travelling Blogs World Wide" />

        <Link rel="canonical" href={`${DOMAIN}${router.pathname}`} />
        <meta
          property="og:title"
          content={`Best travel blogging site for suggestions | ${APP_NAME}`}
        />
        <meta property="og:description" content="Best Travel Blogs online and flight booking systems" />
        <meta property="og:type" content="website" />
      </Head>
    );
  };

  const showAllCategories = () => {
    return categories.map((c, i) => (
      <Link href={`/categories/${c.slug}`} key={i}>
        <a className="btn btn-primary mr-1 ml-1 mt-3">{c.name}</a>
      </Link>
    ));
  };

  const showAllTags = () => {
    return tags.map((t, i) => (
      <Link href={`/tags/${t.slug}`} key={i}>
        <a className="btn btn-secondary mr-1 ml-1 mt-3">{t.name}</a>
      </Link>
    ));
  };

  const showAllBlogs = () => {
    return blogs.map((blog, i) => (
      <article key={i}>
        <BlogCard blog={blog} />
      </article>
    ));
  };

  return (
    <React.Fragment>
      <Layout>
        <main>
          <div className="container-fluid">
            <header>
              <div className="col-md-12 pt-3">
                <h1 className="display-4 font-weight-bold text-center">
                  Travel Blogs
                </h1>
              </div>
              <section className="text-center">
                {showAllCategories()}
                <br />
                {showAllTags()}
              </section>
            </header>
          </div>
          <div className="container-fluid">
            <div className="row">
              <div className="col-md-12">{showAllBlogs()}</div>
            </div>
          </div>
        </main>
      </Layout>
    </React.Fragment>
  );
};

Blogs.getInitialProps = () => {
  return listBlogsWithCategoriesAndTags().then(data => {
    if (data.error) {
      console.log(data.error);
    } else {
      return {
        blogs: data.blogs,
        categories: data.categories,
        tags: data.tags,
        size: data.size
      };
    }
  });
};

export default withRouter(Blogs); //getInitialProps
