import Link from "next/link";
import { useState, useEffect } from "react";
import Router from "next/router";
import { getCookie, isAuth } from "../../actions/auth";
import { list, removeBlog } from "../../actions/blog";
import moment from "moment";

const ReadBlogs = () => {
  const [blogs, setBlogs] = useState([]);
  const [message, setMessage] = useState("");
  const token = getCookie("token");

  useEffect(() => {
    loadBlogs();
  }, []);

  const loadBlogs = () => {
    list().then(data => {
      if (data.error) {
        console.log(data.error);
      } else {
        setBlogs(data);
      }
    });
  };


const deleteConfirm =(slug)=>{
    let answer = window.confirm('Are you sure you want to delete your blog')
    if(answer){
        deleteBlog(slug)
    }
}


const deleteBlog =(slug)=>{
    removeBlog(slug, token).then(data=>{
        if(data.error){
            console.log(data.error)
        }
        else{
            setMessage(data.message)
            loadBlogs()
        }
    })
}

const showUpdateButton=(blog)=>{
  if(isAuth()&& isAuth().role === 0){
    return (
      <Link href ={`/user/crud/blog/${blog.slug}`} />
    )
  }
}


  const showAllBlogs = () => {
    return blogs.map((blog, i) => {
      return (
        <div key={i} className="mt-5 pb-5">
          <h3>{blog.title}</h3>
          <p className="mark">
            Written by {blog.postedBy.name} | Published on{" "}
            {moment(blog.updatedAt).fromNow()}
          </p>
          <button className="btn btn-sm btn-danger" onClick={()=>deleteConfirm(blog.slug)}>Delete</button>
          {showUpdateButton()}
        </div>
      );
    });
  };

  return (
    <React.Fragment>


        <div className="row">
            <div className="col-md-12">
              {message && <div className="aler alert-warning">{message}</div>}
            {showAllBlogs()}
            </div>
        </div>

    </React.Fragment>
  );
};

export default ReadBlogs;
