import React from "react";

import Link from "next/link";

const Footer = props => {
  return (
    <div className="mt-5 pt-5 pb-5 footer">
      <div className="container">
        <div className="row">
          <div className="col-lg-5 col-xs-12 about-company">
            <h2 className="mb-3" style={{fontSize:'3vh'}}>Travel Blog</h2>
            <p className="pr-5 text-white-50 mb-3" style={{fontSize:'2.1vh'}}>
              Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nullam ac
              ante mollis quam tristique convallis{" "}
            </p>
            <p>
              <a href="#">
                <i className="fa fa-facebook-square mr-1"></i>
              </a>
              <a href="#">
                <i className="fa fa-linkedin-square"></i>
              </a>
            </p>
          </div>
          <div className="col-lg-3 col-xs-12 links">
            <h4 className="mt-lg-0 mt-sm-3" style={{fontSize:'2.1vh'}}>Links</h4>
            <ul className="m-0 p-0">
              <li>
                <a href="#">Lorem ipsum</a>
              </li>
              <li>
                <a href="#">Nam mauris velit</a>
              </li>
              <li>
                <a href="#">Etiam vitae mauris</a>
              </li>
              <li>
                <a href="#">Fusce scelerisque</a>
              </li>
              <li>
                <a href="#">Sed faucibus</a>
              </li>
              <li>
                <a href="#">Mauris efficitur nulla</a>
              </li>
            </ul>
          </div>
          <div className="col-lg-4 col-xs-12 location">
            <h4 className="mt-lg-0 mt-sm-4">Location</h4>
            <p>22, Lorem ipsum dolor, consectetur adipiscing</p>
            <p className="mb-0">
              <i className="fa fa-phone mr-3"></i>0431125132
            </p>
            <p>
              <i className="fa fa-envelope-o mr-3"></i>info@travelblog.com
            </p>
          </div>
        </div>
        <div className="row mt-5">
          <div className="col copyright">
            <p className="">
              <small className="text-white-50">
                © 2020. Travel Blog All Rights Reserved.
              </small>
            </p>
          </div>
        </div>
      </div>
      <style jsx>{`
        .footer {
          background: #2D73C0 ;
          color: white;
        }
        .footer .links ul {
          list-style-type: circle;
        }
        .footer .links li a {
          color: white;
          -webkit-transition: color 0.2s;
          transition: color 0.2s;
        }
        .footer .links li a:hover {
          text-decoration: none;
          color: #4180cb;
        }
        .footer .about-company i {
          font-size: 25px;
        }
        .footer .about-company a {
          color: white;
          -webkit-transition: color 0.2s;
          transition: color 0.2s;
        }
        .footer .about-company a:hover {
          color: #4180cb;
        }
        .footer .location i {
          font-size: 18px;
        }
        .footer .copyright p {
          border-top: 1px solid rgba(255, 255, 255, 0.1);
        }
      `}</style>
    </div>
  );
};

export default Footer;
