import Link from "next/link";
import renderHTML from "react-render-html";
import moment from "moment";
import { API } from "../../config";
import ReactImageFallback from "react-image-fallback";


const BlogCard3 = ({ blog }) => {
    // const showBlogCategories = blog =>
    //   blog.categories.map((c, i) => (
    //     <Link key={i} href={`/categories/${c.slug}`}>
    //       <button id="blogcard2-cat">{c.name}</button>
    //     </Link>
    //   ));

    return (
        <div className="blog-card">
        <div className="meta">
          <div className="photo" >
          <Link href={`/blogs/${blog.slug}`}>
          <ReactImageFallback
            className="img img-fluid card-img-top rounded-0"
            style={{ height: "180px", width: "100%" }}
            fallbackImage="/static/default.svg"
            src={`${API}/blog/photo/${blog.slug}`}
            alt={blog.title}
          />
        </Link>
        </div>
          <ul className="details">
            <li className="author">  
            <Link href={`/profile/${blog.postedBy.username}`}>
            <a>{blog.postedBy.username}</a>
          </Link></li>
            <li className="date">{moment(blog.updatedAt).fromNow()}</li>
            {/* <li className="tags">
              <ul>
                <li><a href="#">Learn</a></li>
                <li><a href="#">Code</a></li>
                <li><a href="#">HTML</a></li>
                <li><a href="#">CSS</a></li>
              </ul>
            </li> */}
          </ul>
        </div>
        <div id="description">
          <h2><Link href={`/blogs/${blog.slug}`}>
                    <a>
                        <h2 className="pt-2 pb-2 font-weight-bold">{blog.title}</h2>
                    </a>
                </Link></h2>

          <div> {renderHTML(blog.excerpt)}</div>
          <p className="read-more">
          <Link href={`/blogs/${blog.slug}`}>
            <a className="" style={{ paddingBottom: "3rem" }}>
              Read more
            </a>
          </Link>
          </p>
        </div>

   <style jsx>{`
   h1,
   a {
     font-family: 'Arial';
   }

   ul {
     padding: 0;
   }

   li {
     list-style: none;
     margin: 5px 0;
   }

   a {
     text-decoration: none;
     color: blue;
   }

   a:hover {
     opacity: 0.6;
   }
 `}</style>
 </div>
    );
  };

  export default BlogCard3;
