import Private from "../../components/auth/Private";
import Layout from "../../components/Layout";
import Link from "next/link";
import ReadBlogs from '../../components/crud/ReadBlogs'
import {isAuth} from '../../actions/auth'
import {userPublicProfile} from '../../actions/user'


const UserIndex = (user) => {
  const username = isAuth() && isAuth().username



  return (
    <Layout>
      <Private>
        <div className="container-fluid">
          <div className="row">
            <div className="col-md-12 pt-5 pb-5">
              <h2>Welcome back to {`${username}`}'s Dashboard</h2>
              <p className="text-muted">
                        Joined {moment(user.createdAt).fromNow()}
                      </p>
                      <div className="col-md-4">
                      <img
                        src={`${API}/user/photo/${user.username}`}
                        className="img img-fluid img-thumbnail mb-3"
                        style={{ maxHeight: "100px", maxWidth: "100%" }}
                        alt="user profile"
                      />
                    </div>
            </div>
            <div className="col-md-4">
              <ul className="list-group">
                <li className="list-group-item">
                  <a href="/user/crud/blog">Create Blog</a>
                </li>

                {/* <li className="list-group-item">
                  <Link href="/user/crud/blogs">
                    <a>My Blogs</a>
                  </Link>
                </li> */}

                <li className="list-group-item">
                  <a href="/user/update"> Update Profile</a>
                </li>
              </ul>
            </div>
            {/* <div className="col-md-8">right</div> */}
          </div>
          <ReadBlogs username={username}/>
        </div>
      </Private>
    </Layout>
  );
};



UserIndex.getInitialProps = ({ query }) => {
  return userPublicProfile(query.username).then(data => {
    if (data.error) {
      console.log(data.error);
    } else {
      //   console.log(data);
      return { user: data.user, blogs: data.blogs, query };
    }
  });
};


export default UserIndex;
