const useStyles = makeStyles(theme => ({
  root: {
    flexGrow: 1
  },
  menuButton: {
    marginRight: theme.spacing(2)
  },
  title: {
    flexGrow: 1
  }
}));

const Header = props => {
  const classes = useStyles();
  return (
    <div className={classes.root}>
      <AppBar position="sticky">
        <Toolbar>
          <IconButton
            edge="start"
            className={classes.menuButton}
            color="inherit"
            aria-label="menu"
          >
            <MenuIcon />
          </IconButton>
          <Typography variant="h6" className={classes.title}>
            News
          </Typography>
          <Button color="inherit">
            <Link href="/blogs">
              <a href="/blogs">
                Blogs
              </a>
            </Link>
          </Button>

          {!isAuth() && (
            <Button color="inherit">
              <Link href="/signin">
                <a href="/signin">
                  Signin
                </a>
              </Link>
            </Button>
          )}

          {!isAuth() && (
            <Button color="inherit">
              <Link href="/signup">
                <a href="/signup">
                  Signup
                </a>
              </Link>
            </Button>
          )}

          {isAuth() && isAuth().role !== 1 && (
            <Button color="inherit">
              <Link href="/user">
                <a href="/user">Dashboard</a>
              </Link>
            </Button>
          )}

          {isAuth() && isAuth().role === 1 && (
            <Button color="inherit">
              <Link href="/admin">
                <a href="/admin">Dashboard</a>
              </Link>
            </Button>
          )}

          {isAuth() && (
            <Button color="inherit"  >
              <Link
                // style={{ cursor: "pointer" }}

              >
                <a   onClick={() => signout(() => Router.replace(`/signin`))}>Signout</a>
              </Link>
            </Button>
          )}

          {isAuth() && (
            <Button color="inherit">
              <Link href="/user/crud/blog">
                <a href="/user/crud/blog">
                  Create
                </a>
              </Link>
            </Button>
          )}

          {!isAuth() && (
            <Button color="inherit">
              <Link href="/signin">
                <a href="/signin">
                  Create
                </a>
              </Link>
            </Button>
          )}
        </Toolbar>
      </AppBar>
    </div>
  );
};

export default Header;
