import Layout from "../../components/Layout";
import Link from "next/link";
import Head from "next/head";
import { singleCategory } from "../../actions/category";
import { API, DOMAIN, APP_NAME } from "../../config";
import moment from "moment";
import renderHTML from "react-render-html";
import SmallBlogCard from "../../components/blog/SmallBlogCard";

const Category = ({category,query}) => {

    const head = () => (
        <Head>
            <title>
                {category.name} | {APP_NAME}
            </title>
            <meta name="description" content={`Best programming tutorials on ${category.name}`} />
            <link rel="canonical" href={`${DOMAIN}/categories/${query.slug}`} />
            <meta property="og:title" content={`${category.name}| ${APP_NAME}`} />
            <meta property="og:description" content={`Best programming tutorials on ${category.name}`} />
            <meta property="og:type" content="webiste" />
            <meta property="og:url" content={`${DOMAIN}/categories/${query.slug}`} />
            <meta property="og:site_name" content={`${APP_NAME}`} />

            {/* <meta property="og:image" content={`${DOMAIN}/static/images/seoblog.jpg`} />
            <meta property="og:image:secure_url" content={`${DOMAIN}/static/images/seoblog.jpg`} />
            <meta property="og:image:type" content="image/jpg" />
            <meta property="fb:app_id" content={`${FB_APP_ID}`} /> */}
        </Head>
    );

  return (
    <React.Fragment>
                    {head()}

     <Layout>
                <main>
                    <div className="container-fluid text-center">
                        <header>
                            <div className="col-md-12 pt-3">
  <h1 className="display-4 font-weight-bold">{category.name}</h1>

                            </div>
                        </header>
                    </div>
                </main>
            </Layout>
    </React.Fragment>
  );
};


Category.getInitialProps = ({ query }) => {
    return singleCategory(query.name).then(data => {
        if (data.error) {
            console.log(data.error);
        } else {
            return { category: data, query };
        }
    });
};


export default Category;
