import Layout from "../../components/Layout";
import Link from "next/link";
import Head from "next/head";
import { useState } from "react";
import { listBlogsWithCategoriesAndTags } from "../../actions/blog";
import { API } from "../../config";

const Blogs = ({ blogs, categories, tags, size }) => {
  const showAllBlogs = () => {
    return blogs.map((blog, i) => (
      <article key={i}>
        <div className="lead pb-4">
            <header>
                <Link href={`/blogs/${blog.slug}`}>
                    <a><h2 className=" pt-3 pb-3 font-weight-bold">{blog.title}</h2></a>
                </Link>
            </header>
            <section>
                <p className="mark ml-1 pt-2 pb-2">
                    Written by {blog.postedBy.name} | Published {blog.updatedAt}
                </p>
            </section>
            <section>
                <p>blog categories and tags</p>
            </section>
            <div className="row">
                <div className="col-md-4">
                        image
                </div>
                <div className="col-md-8">
                    <section>
                       <div className="pb-3">
                       {blog.excerpt}
                           </div> 
                        <Link href={`/blogs/${blog.slug}`}>
                            <a className="btn btn-primary pt-2">
                                Read more
                            </a>
                        </Link>
                    </section>
                </div>
            </div>
        </div>
      </article>
    ));
  };

  return (
    <React.Fragment>
      <Layout>
        <main>
          <div className="container-fluid">
            <header>
              <div className="col-md-12 pt-3">
                <h1 className="display-4 font-weight-bold text-center">
                  Travel Blogs
                </h1>
              </div>
              <section>
                <p>show cat and tag</p>
              </section>
            </header>
          </div>
          <div className="container-fluid">
            <div className="row">
  <div className="col-md-12">{showAllBlogs()}</div>
            </div>
          </div>
        </main>
      </Layout>
    </React.Fragment>
  );
};

Blogs.getInitialProps = () => {
  return listBlogsWithCategoriesAndTags().then(data => {
    if (data.error) {
      console.log(data.error);
    } else {
      return {
        blogs: data.blogs,
        categories: data.categories,
        tags: data.tags,
        size: data.size
      };
    }
  });
};

export default Blogs; //getInitialProps
