import Link from "next/link";
import renderHTML from "react-render-html";
import moment from "moment";
import { API } from "../../config";
import ReactImageFallback from "react-image-fallback";

const BlogCard3 = ({ blog }) => {
  // const showBlogCategories = blog =>
  //   blog.categories.map((c, i) => (
  //     <Link key={i} href={`/categories/${c.slug}`}>
  //       <button id="blogcard2-cat">{c.name}</button>
  //     </Link>
  //   ));

  return (
    <div className="card">
      <div className="card-image">
        <figure className="image is-4by3">
          <ReactImageFallback
            className="img img-fluid"
            style={{ height: '180px', width: '100%' }}
            fallbackImage="/static/default.svg"
            src={`${API}/blog/photo/${blog.slug}`}
            alt={blog.title}
          />
        </figure>
      </div>
      <div className="card-content">
        <div className="media">
          <div className="media-left">
            <figure className="image is-48x48">
              <ReactImageFallback
                className="img img-fluid rounded-circle mr-3"
                  style={{ height: "50px", width: "50px" }}
                fallbackImage="/static/default.svg"
                src={`${API}/user/photo/${blog.postedBy.username}`}
                alt={blog.title}
              />
            </figure>
          </div>
          <div className="media-content">
            <div className="title is-4">{blog.title}</div>
            <p className="subtitle is-6">
              @
              <Link href={`/profile/${blog.postedBy.username}`}>
                <a>{blog.postedBy.username}</a>
              </Link>
            </p>
          </div>
        </div>

        <div className="content">
          {renderHTML(blog.excerpt)}
          <br />
          <time>{moment(blog.updatedAt).fromNow()}</time>
        </div>
      </div>
    </div>
  );
};

export default BlogCard3;
