import Link from "next/link";
import renderHTML from "react-render-html";
import moment from "moment";
import { API } from "../../config";
import ReactImageFallback from "react-image-fallback";
import { Card, CardTitle, CardText, CardImg, CardImgOverlay } from 'reactstrap';

const BlogCard3 = ({ blog }) => {
  return (
    <div>
      <Card inverse>

        <ReactImageFallback
        className="img img-fluid"
        style={{ height: '30vh'}}
        fallbackImage="/static/default.svg"
        src={`${API}/blog/photo/${blog.slug}`}
        alt={blog.title}
      />

        <CardImgOverlay>
          <CardTitle><Link href={`/blogs/${blog.slug}`}>
            <a>
              <h2 className="pt-2 pb-2 font-weight-bold">{blog.title}</h2>
            </a>
          </Link></CardTitle>

          <CardText>
            <small className="text-muted">{moment(blog.updatedAt).fromNow()}</small>
          </CardText>
        </CardImgOverlay>
      </Card>
    </div>
  );
};

export default BlogCard3;
