import { Button } from "@material-ui/core";
import { follow, unfollow } from "../../actions/user";
import {withRouter} from 'next/router'

const FollowProfileButton = ({router}) => {
  const followClick = () => {
    this.onButtonClick(follow);
  };

  const unfollowClick = () => {
    this.onButtonClick(unfollow);
  };

  return (

    <div>
      {JSON.stringify(router)}
      {!router.user.following ? (
        <Button
          onClick={followClick}
          variant="contained"
          color="primary"
          size="large"
          type="submit"
        >
          Follow
        </Button>
      ) : (
        <Button
          onClick={unfollowClick}
          variant="contained"
          color="secondary"
          size="large"
          type="submit"
        >
          Unfollow
        </Button>
      )}
    </div>
  );
};




export default withRouter(FollowProfileButton);
