import Layout from "../components/Layout";
import Link from 'next/link'
import Slider from 'react-animated-slider'
import 'react-animated-slider/build/index'
import '../static/css'

const content = [
  {
      title: 'Vulputate Mollis Ultricies Fermentum Parturient',
      button: 'Read More',
      image: require('../static/slide1.jpg'),
  },
  {
      title: 'Tortor Dapibus Commodo Aenean Quam',
      button: 'Discover',
      image: require('../static/slide2.jpg'),
  },
  {
      title: 'Tortor Dapibus Commodo Aenean Quam',
      button: 'Discover',
      image: require('../static/slide3.jpg'),
  }
];

const Index = () => {
  return (
    <Layout>
      <h2 className="text-center">Home Page</h2>
      <Slider className="slider-wrapper" infinite autoplay>
                        {content.map((item, index) => (
                            <div
                                key={index}
                                className="slider-content"
                                style={{ background: `url('${item.image}') no-repeat center center` }}
                            >
                                <div className="inner">
                                    <h1>{item.title}</h1>
                                    <p>{item.description}</p>
                                    <button className="site-button">{item.button}</button>
                                </div>
                            </div>
                        ))}
                    </Slider>

    </Layout>
  );
};

export default Index;
