import Layout from "../components/Layout";
import Slider from "../components/Slider";
import RecentCard from "../components/Recent";
import { listBlogsWithCategoriesAndTags } from "../actions/blog";
import Head from "next/head";


const Index = ({ blogs }) => {

  const head=()=>(
    <Head>
    <meta http-equiv="Content-Type" content="text/html; charset=UTF-8"/>
     <title>TravelBlog</title>

     <meta name="viewport" content="width=device-width, initial-scale=1.0"/>

     <link rel="stylesheet" type="text/css" href="/static/jquery/reset.css"/>
     <link rel="stylesheet prefetch" href="https://cdnjs.cloudflare.com/ajax/libs/bootstrap-datepicker/1.6.0/css/bootstrap-datepicker3.standalone.css"/>
     <link rel="stylesheet" type="text/css" href="/static/jquery/style.css?v_1207"/>

     <script src="/static/jquery/jquery.js"></script>
     <script src="https://cdnjs.cloudflare.com/ajax/libs/jquery-dateFormat/1.0/jquery.dateFormat.js"></script>
     <script src="https://cdnjs.cloudflare.com/ajax/libs/bootstrap-datepicker/1.6.0/js/bootstrap-datepicker.min.js"></script>
     <script src="/static/jquery/jquery.twidget.js?v_1207"></script>


     <link href="https://fonts.googleapis.com/css?family=Open+Sans:400,300,400italic,300italic,600,600italic,700,700italic,800,800italic" rel="stylesheet" type="text/css"/>
     <link href='https://fonts.googleapis.com/css?family=Open+Sans&subset=latin,cyrillic' rel='stylesheet' type='text/css'/>
     {/* <script>
        $('#twidget').twidget({
            locale: 'en',
            marker: 78606,
            type: 'hotel'
        });
</script> */}
     </Head>
  );

  const showAllBlogs = () => {
    return blogs.map((blog, i) => (
      <div className="col-md-4 " key={i}>
        <article>
          <RecentCard blog={blog} />
        </article>
      </div>
    ));
  };

  return (
    <React.Fragment>
      {head()}
      <Layout>
        <Slider />
        <div class="twidget-container" id="twidget"></div>
        <div className="container">
          <h4 className="text-center pt-5 pb-5 h2">Lastest Blogs</h4>
          <div className="row">{showAllBlogs()}</div>
        </div>
      </Layout>
    </React.Fragment>
  );
};

Index.getInitialProps = () => {
  let skip = 0;
  let limit = 6;
  return listBlogsWithCategoriesAndTags(skip, limit).then(data => {
    if (data.error) {
      console.log(data.error);
    } else {
      // console.log(data);
      return {
        blogs: data.blogs
        // blog: data,
      };
    }
  });
};

export default Index;
