import Link from "next/link";
import renderHTML from "react-render-html";
import moment from "moment";
import { API } from "../../config";
import ReactImageFallback from "react-image-fallback";
import styled from 'styled-components';

const button = button.styled`
padding:'0.5rem'
`




const BlogCard2 = ({ blog }) => {
  const showBlogCategories = blog =>
    blog.categories.map((c, i) => (
      <Link key={i} href={`/categories/${c.slug}`}>
        <button  >{c.name}</button>
      </Link>
    ));



  return (
    <div className="card promoting-card" style={{height:'450px'}}>
      <div className="card-body d-flex flex-row">
        <ReactImageFallback
          className="img img-fluid rounded-circle mr-3"
          style={{ height: "50px", width: "50px" }}
          fallbackImage="/static/default.svg"
          src={`${API}/user/photo/${blog.postedBy.username}`}
          alt={blog.title}
        />
        <div>
          <Link href={`/profile/${blog.postedBy.username}`}>
            <a>{blog.postedBy.username}</a>
          </Link>

          <p className="card-text">
            <i class="far fa-clock pr-2"></i>
            {moment(blog.updatedAt).fromNow()}
          </p>
        </div>
      </div>

      <div className="view overlay">
      <Link href={`/blogs/${blog.slug}`}>
        <ReactImageFallback
          className="img img-fluid card-img-top rounded-0"
          style={{ height: "180px", width: "100%" }}
          fallbackImage="/static/default.svg"
          src={`${API}/blog/photo/${blog.slug}`}
          alt={blog.title}
        />
        </Link>
        {showBlogCategories(blog)}
      </div>

      <div class="card-body" style={{}}>
        <div class="" style={{ height: "70px", overflow: "hidden" }}>
          {renderHTML(blog.excerpt)}
        </div>
        <div className="mt-2">
          <Link href={`/blogs/${blog.slug}`}>
            <a className="" style={{paddingBottom:'3rem'}} >
              Read more
            </a>
          </Link>

        </div>

      </div>
    </div>
  );
};

export default BlogCard2;
