import Link from "next/link";
import renderHTML from "react-render-html";
import { useState, useEffect } from "react";
import { listSearch } from "../../actions/blog";
import { InputGroup, InputGroupAddon, Input } from 'reactstrap';

const Search = () => {
  const [values, setValues] = useState({
    search: undefined,
    results: [],
    searched: false,
    message: ""
  });

  const { search, results, searched, message } = values;

  const searchSubmit = e => {
    e.preventDefault();
    listSearch({ search }).then(data => {
      setValues({
        ...values,
        searched: true,
        results: data,
        message: `${data.length} blogs found`
      });
    });
  };

  const handleChange = e => {
    setValues({
      ...values,
      search: e.target.value,
      searched: false,
      results: []
    });
  };

  const searchedBlogs = (results = []) => {
    return (
      <div className="jumbotron bg-white">
        {message && <p className="pt-4 text-muted font-italic">{message}</p>}
        {results.map((blog, i) => {
          return (
            <div key={i}>
              <Link href={`/blogs/${blog.slug}`}>
                <a className="text-primary">{blog.title}</a>
              </Link>
            </div>
          );
        })}
      </div>
    );
  };

  const searchForm = () => (


 <InputGroup onSubmit={searchSubmit}>
        <Input onChange={handleChange}/>
        <InputGroupAddon addonType="append">
          <Button type="submit" color="secondary">To the Right!</Button>
        </InputGroupAddon>

        </InputGroup>

    // <form
    //   className="row pl-0 pr-0"
    //   onSubmit={searchSubmit}
    //   id="search-form"
    //   style={{ maxWidth: "100%" }}
    // >
    //   <div className="col-md-10 col-sm-2">
    //     <input
    //       type="search"
    //       className="form-control"
    //       placeholder="SearchBlogs"
    //       onChange={handleChange}
    //     />
    //   </div>
    //   <div className="col-md-2 col-sm-2">
    //     <button type="submit">
    //       <i className="fa fa-search" id="search-button"></i>
    //     </button>
    //   </div>
    // </form>
  );

  return (
    <div className="container-fluid">
      <div className="pb-5">{searchForm()}</div>
      {searched && (
        <div style={{ marginTop: "-120px", marginBottom: "-80px" }}>
          {searchedBlogs(results)}
        </div>
      )}
    </div>
  );
};

export default Search;
