import React, { useState } from "react";
import { APP_NAME } from "../config";
import Link from "next/link";
import { signout, isAuth } from "../actions/auth";
import Nprogress from "nprogress";
import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  NavbarText
} from "reactstrap";
import Router from "next/router";

import { fade, makeStyles } from "@material-ui/core/styles";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import IconButton from "@material-ui/core/IconButton";
import Typography from "@material-ui/core/Typography";
import InputBase from "@material-ui/core/InputBase";
import Badge from "@material-ui/core/Badge";
import MenuItem from "@material-ui/core/MenuItem";
import Menu from "@material-ui/core/Menu";
import MenuIcon from "@material-ui/icons/Menu";
import SearchIcon from "@material-ui/icons/Search";
import AccountCircle from "@material-ui/icons/AccountCircle";
import MailIcon from "@material-ui/icons/Mail";
import NotificationsIcon from "@material-ui/icons/Notifications";
import MoreIcon from "@material-ui/icons/MoreVert";
import { Button } from "@material-ui/core";

Router.onRouteChangeStart = url => Nprogress.start();
Router.onRouteChangeComplete = url => Nprogress.done();
Router.onRouteChangeError = url => Nprogress.done();

const Header = props => {
  const [isOpen, setIsOpen] = useState(false);

  const toggle = () => setIsOpen(!isOpen);

  return (
    <React.Fragment>
      <Navbar color="light" light expand="md" className="sticky-top">
        <div className="container">
        <Link href="/">
          <NavLink className="font-weight-bold">Travel Blog</NavLink>
        </Link>
        <NavbarToggler onClick={toggle} />
        <Collapse isOpen={isOpen} navbar>
          <Nav className="ml-auto" navbar >
            <React.Fragment>
              <NavItem>
                <Link href="/blogs">
                  <NavLink>Blogs</NavLink>
                </Link>
              </NavItem>
            </React.Fragment>

            <UncontrolledDropdown nav inNavbar>
              <DropdownToggle nav caret>
                Categories
              </DropdownToggle>
              <DropdownMenu right>
                <DropdownItem>
                  <Link href="/categories/asia">
                    <NavLink>Asia</NavLink>
                  </Link>
                </DropdownItem>
                <DropdownItem>
                  <Link href="/categories/europe">
                    <NavLink>Europe</NavLink>
                  </Link>
                </DropdownItem>
                <DropdownItem>
                  <Link href="/categories/india">
                    <NavLink>India</NavLink>
                  </Link>
                </DropdownItem>
                <DropdownItem>
                  <Link href="/categories/north-america">
                    <NavLink>North America</NavLink>
                  </Link>
                </DropdownItem>
                <DropdownItem>
                  <Link href="/categories/south-america">
                    <NavLink>South America</NavLink>
                  </Link>
                </DropdownItem>
                <DropdownItem>
                  <Link href="/categories/middle-east">
                    <NavLink>Middle East</NavLink>
                  </Link>
                </DropdownItem>
                <DropdownItem>
                  <Link href="/categories/oceania">
                    <NavLink>Oceania</NavLink>
                  </Link>
                </DropdownItem>
                <DropdownItem>
                  <Link href="/categories/africa">
                    <NavLink>Africa</NavLink>
                  </Link>
                </DropdownItem>
              </DropdownMenu>
            </UncontrolledDropdown>

            {!isAuth() && (
              <React.Fragment>
                <NavItem>
                  <Link href="/signin">
                    <NavLink>Signin</NavLink>
                  </Link>
                </NavItem>
              </React.Fragment>
            )}

            {!isAuth() && (
              <React.Fragment>
                <NavItem>
                  <Link href="/signup">
                    <NavLink>Signup</NavLink>
                  </Link>
                </NavItem>
              </React.Fragment>
            )}

            {isAuth() && isAuth().role !== 1 && (
              <NavItem>
                <Link href="/user">
                  {/* <NavLink>{`${isAuth().name}'s Dashboard`}</NavLink> */}
                  <NavLink>Dashboard</NavLink>
                </Link>
              </NavItem>
            )}

            {isAuth() && isAuth().role === 1 && (
              <NavItem>
                <Link href="/admin">
                  {/* <NavLink>{`${isAuth().name}'s Dashboard`}</NavLink> */}
                  <NavLink>Dashboard</NavLink>
                </Link>
              </NavItem>
            )}

            {isAuth() && (
              <NavItem>
                <NavLink
                  style={{ cursor: "pointer" }}
                  onClick={() => signout(() => Router.replace(`/signin`))}
                >
                  Signout
                </NavLink>
              </NavItem>
            )}

            {isAuth() && (
              <NavItem>
                <Link href="/user/crud/blog">
                  <NavLink active className="font-weight-bold" id="blog-nav">
                    <a
                      href="/user/crud/blog"
                      style={{ textDecoration: "none", color:'white'}}
                    >
                      Create
                    </a>
                  </NavLink>
                </Link>
              </NavItem>
            )}

            {!isAuth() && (
              <NavItem>
                <Link href="/signin">
                  <NavLink active className="font-weight-bold" id="blog-nav">
                    <a href="/signin" style={{ textDecoration: "none", color:'white' }}>
                      Create
                    </a>
                  </NavLink>
                </Link>
              </NavItem>
            )}

          </Nav>
        </Collapse>
        </div>
      </Navbar>
      {/* <Search/> */}
    </React.Fragment>
  );
};


export default Header;
