import Link from "next/link";
import { useState, useEffect } from "react";
import Router from "next/router";
import dynamic from "next/dynamic";
import { withRouter } from "next/router";
import { getCookie, isAuth } from "../../actions/auth";
import { getCategories } from "../../actions/category";
import { getTags } from "../../actions/tag";
import { singleBlog, updateBlog } from "../../actions/blog";
import "../../node_modules/react-quill/dist/quill.snow.css";
import { QuillModules, QuillFormats } from "../../helpers/quill";
import { DOMAIN } from "../../config";
//close ssr
const ReactQuill = dynamic(() => import("react-quill"), { ssr: false });

const BlogUpdate = ({ router }) => {
  const token = getCookie("token");

  const [body, setBody] = useState("");
  const [values, setValues] = useState({
    title: "",
    error: "",
    success: "",
    formData: "",
    title: "",
    body: ""
  });

  const [categories, setCategories] = useState([]);
  const [tags, setTags] = useState([]);

  const [checkedCat, setCheckedCat] = useState([]); //categories
  const [checkedTag, setCheckedTag] = useState([]); //tag

  const { error, success, formData, title } = values;

  useEffect(() => {
    setValues({ ...values, formData: new FormData() });
    initBlog();
    initCategories();
    initTags();
  }, [router]);

  const initCategories = () => {
    getCategories().then(data => {
      if (data.error) {
        setValues({ ...values, error: data.error });
      } else {
        setCategories(data);
      }
    });
  };

  const initTags = () => {
    getTags().then(data => {
      if (data.error) {
        setValues({ ...values, error: data.error });
      } else {
        setTags(data);
      }
    });
  };

  const handleToggleCat = c => () => {
    setValues({ ...values, error: "" });

    //return the first index or -1
    const clickedCategory = checkedCat.indexOf(c);
    const all = [...checkedCat];
    if (clickedCategory === -1) {
      all.push(c);
    } else {
      all.splice(clickedCategory, 1);
    }
    console.log(all);
    setCheckedCat(all);
    formData.set("categories", all);
  };

  const handleToggleTag = t => () => {
    setValues({ ...values, error: "" });

    //return the first index or -1
    const clickedTags = checkedTag.indexOf(t);
    const all = [...checkedTag];
    if (clickedTags === -1) {
      all.push(t);
    } else {
      all.splice(clickedTags, 1);
    }
    console.log(all);
    setCheckedTag(all);
    formData.set("tags", all);
  };

  const showCategories = () => {
    return (
      categories &&
      categories.map((c, i) => (
        <li key={i} className="list-unstyled">
          <input
            onChange={handleToggleCat(c._id)}
            checked={findOutCategory(c._id)}
            type="checkbox"
            className="mr-2"
          />
          <label className="form-check-label">{c.name}</label>
        </li>
      ))
    );
  };

  const showTags = () => {
    return (
      tags &&
      tags.map((t, i) => (
        <li key={i} className="list-unstyled">
          <input
            onChange={handleToggleTag(t._id)}
            checked={findOutTag(t._id)}
            type="checkbox"
            className="mr-2"
          />
          <label className="form-check-label">{t.name}</label>
        </li>
      ))
    );
  };

  const findOutCategory = c => {
    const result = checkedCat.indexOf(c);
    if (result !== -1) {
      return true;
    } else return false;
  };

  const findOutTag = t => {
    const result = checkedTag.indexOf(t);
    if (result !== -1) {
      return true;
    } else return false;
  };

  const initBlog = () => {
    if (router.query.slug) {
      singleBlog(router.query.slug).then(data => {
        if (data.error) {
          console.log(data.error);
        } else {
          setValues({ ...values, title: data.title });
          setBody(data.body);
          setCategoriesArray(data.categories);
          setTagsArray(data.tags);
        }
      });
    }
  };

  const setCategoriesArray = blogCategories => {
    let ca = [];
    blogCategories.map((c, i) => ca.push(c._id));
    return setCheckedCat(ca);
  };

  const setTagsArray = blogTags => {
    let ta = [];
    blogTags.map((t, i) => ta.push(t._id));
    return setCheckedTag(ta);
  };

  const handleBody = e => {
    setBody(e);
    formData.set("body", e);
  };

  const editBlog = e => {
    e.preventDefault();
    updateBlog(formData, token, router.query.slug).then(data => {
      if (data.error) {
        setValues({ ...values, error: data.error });
      } else {
        setValues({
          ...values,
          title: "",
          success: `Blog titled "${data.title}" is successfully updated`
        });
        if (isAuth() && isAuth().role === 1) {
          Router.replace(`${DOMAIN}/admin/crud/${router.query.slug}`);
        } else if (isAuth() && isAuth().role === 1) {
          Router.replace(`${DOMAIN}/user/crud/${router.query.slug}`);
        }
      }
    });
  };

  const handleChange = name => e => {
    const value = name === "photo" ? e.target.files[0] : e.target.value;
    formData.set(name, value);

    setValues({ ...values, [name]: value, formData, error: "" });
  };

  const updateBlogForm = () => (
    <form onSubmit={editBlog}>
      <div className="form-group">
        <label className="text-muted">Title</label>
        <input
          type="text"
          className="form-control"
          onChange={handleChange("title")}
          value={title}
          placeholder="Type your blog title"
        ></input>
      </div>
      <div className="form-group">
        <ReactQuill
          modules={QuillModules}
          formats={QuillFormats}
          value={body}
          placeholder="Write your experience..."
          onChange={handleBody}
        />
      </div>
      <div>
        <button className="btn btn-primary" type="submit">
          Update
        </button>
      </div>
    </form>
  );

  return (
    <div className="container-fluid pb-5">
      <div className="row">
        <div className="col-md-6">
          <p>Create Blog Form</p>
          <div className="pb-3">
            {/* {showError()}
            {showSuccess()} */}
            <p>show success and error</p>
          </div>
          {updateBlogForm()}
        </div>
        <div className="col-md-6">
          <div>
            <div className="form-group mb-3">
              <h2>Featured Image</h2>
              <hr />

              <small className="text-muted mr-2">Max Size: 1MB</small>
              <label className="btn btn-outline-info">
                Upload Featured Image
                <input
                  onChange={handleChange("photo")}
                  type="file"
                  accept="image/*"
                  hidden
                />
              </label>
            </div>
          </div>
          <div className="mb-3">
            <h2>Categories</h2>
            <hr />
            <ul style={{ maxHeight: "200px", overflowY: "scroll" }}>
              {showCategories()}
            </ul>
          </div>
          <div>
            <h2>Tags</h2>
            <hr />
            <ul style={{ maxHeight: "200px", overflowY: "scroll" }}>
              {showTags()}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default withRouter(BlogUpdate);
