import React, { useState,useRef, useEffect, createRef} from "react";
import { APP_NAME } from "../config";
import Link from "next/link";
import { signout, isAuth } from "../actions/auth";
import Nprogress from "nprogress";
import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  NavbarText
} from "reactstrap";
import Router from "next/router";


Router.onRouteChangeStart = url => Nprogress.start();
Router.onRouteChangeComplete = url => Nprogress.done();
Router.onRouteChangeError = url => Nprogress.done();

const Header = props => {

  const [isOpen, setIsOpen] = useState(false);
  const [navExpanded, setNavExpanded] = useState(false);

  const closeNav = () => setNavExpanded(!navExpanded);
  const toggle = () => setIsOpen(!isOpen);
  const wrapperRef = useRef();


  const useOnClickOutside = (ref, handler) => {
    useEffect(() => {
      const listener = event => {
        if (!ref.current || ref.current.contains(event.target)) {
          return;
        }
        handler(event);
      };
      document.addEventListener('mousedown', listener);
      return () => {
        document.removeEventListener('mousedown', listener);
      };
    },
    [ref, handler],
    );
  };
  useOnClickOutside(wrapperRef, () => setIsOpen(false));

  return (
    // open && (
      <div  ref={wrapperRef}>
    <React.Fragment>
      <Navbar color="light" light expand="md" className="sticky-top">
        <div className="container">
          <Link href="/">
            <NavLink className="font-weight-bold">Travel Blog</NavLink>
          </Link>
          <NavbarToggler onClick={toggle} />
          <Collapse isOpen={isOpen} setIsOpen={setIsOpen} navbar>
            <Nav className="ml-auto" navbar onSelect={closeNav}>
              <React.Fragment>
                <NavItem>
                  <Link href="/blogs">
                    <NavLink style={{fontSize:'2.2vh'}}>Blogs</NavLink>
                  </Link>
                </NavItem>
              </React.Fragment>

              <UncontrolledDropdown nav inNavbar>
                <DropdownToggle nav caret style={{fontSize:'2.2vh'}}>
                  Categories
                </DropdownToggle>
                <DropdownMenu right>
                  <DropdownItem>
                    <Link href="/categories/asia">
                      <NavLink>Asia</NavLink>
                    </Link>
                  </DropdownItem>
                  <DropdownItem>
                    <Link href="/categories/europe">
                      <NavLink>Europe</NavLink>
                    </Link>
                  </DropdownItem>
                  <DropdownItem>
                    <Link href="/categories/india">
                      <NavLink>India</NavLink>
                    </Link>
                  </DropdownItem>
                  <DropdownItem>
                    <Link href="/categories/north-america">
                      <NavLink>North America</NavLink>
                    </Link>
                  </DropdownItem>
                  <DropdownItem>
                    <Link href="/categories/south-america">
                      <NavLink>South America</NavLink>
                    </Link>
                  </DropdownItem>
                  <DropdownItem>
                    <Link href="/categories/middle-east">
                      <NavLink>Middle East</NavLink>
                    </Link>
                  </DropdownItem>
                  <DropdownItem>
                    <Link href="/categories/oceania">
                      <NavLink>Oceania</NavLink>
                    </Link>
                  </DropdownItem>
                  <DropdownItem>
                    <Link href="/categories/africa">
                      <NavLink>Africa</NavLink>
                    </Link>
                  </DropdownItem>
                </DropdownMenu>
              </UncontrolledDropdown>

              {!isAuth() && (
                <React.Fragment>
                  <NavItem>
                    <Link href="/signin">
                      <NavLink style={{fontSize:'2.2vh'}}>Signin</NavLink>
                    </Link>
                  </NavItem>
                </React.Fragment>
              )}

              {!isAuth() && (
                <React.Fragment>
                  <NavItem>
                    <Link href="/signup">
                      <NavLink style={{fontSize:'2.2vh'}}>Signup</NavLink>
                    </Link>
                  </NavItem>
                </React.Fragment>
              )}

              {isAuth() && isAuth().role !== 1 && (
                <NavItem>
                  <Link href="/user">
                    {/* <NavLink>{`${isAuth().name}'s Dashboard`}</NavLink> */}
                    <NavLink style={{fontSize:'2.2vh'}}>Dashboard</NavLink>
                  </Link>
                </NavItem>
              )}

              {isAuth() && isAuth().role === 1 && (
                <NavItem>
                  <Link href="/admin">
                    {/* <NavLink>{`${isAuth().name}'s Dashboard`}</NavLink> */}
                    <NavLink style={{fontSize:'2.2vh'}}>Dashboard</NavLink>
                  </Link>
                </NavItem>
              )}

              {isAuth() && (
                <NavItem>

                    <NavLink
                      style={{ cursor: "pointer", fontSize:'2.2vh' }}
                      onClick={() => signout(() => Router.replace(`/signin`))}
                    >
                      Signout
                    </NavLink>

                </NavItem>
              )}

              {isAuth() && (
                <NavItem>
                  <Link href="/user/crud/blog">
                    <NavLink active className="font-weight-bold" id="blog-nav" style={{fontSize:'2.2vh'}}>
                      {/* <a
                      href="/user/crud/blog"
                      style={{ textDecoration: "none", color:'white'}}
                    > */}
                      Create
                      {/* </a> */}
                    </NavLink>
                  </Link>
                </NavItem>
              )}

              {!isAuth() && (
                <NavItem>
                  <Link href="/signin">
                    <NavLink active className="font-weight-bold" id="blog-nav" style={{fontSize:'2.2vh'}}>
                      {/* <a href="/signin" style={{ textDecoration: "none", color:'white' }}> */}
                      Create
                      {/* </a> */}
                    </NavLink>
                  </Link>
                </NavItem>
              )}
            </Nav>
          </Collapse>
        </div>
      </Navbar>
    </React.Fragment>
    </div>
    // )
  );
};

export default Header;
