import { Button } from "@material-ui/core";
import { follow, unfollow } from "../../actions/user";
import {withRouter} from 'next/router'

const FollowProfileButton = ({user}) => {
  const followClick = () => {

    onButtonClick(follow);
  };

  const unfollowClick = () => {
    onButtonClick(unfollow);
  };

  return (

    <div>


        <Button
          onClick={followClick}
          variant="contained"
          color="primary"
          size="large"
          type="submit"
        >
          Follow
        </Button>

        <Button
          onClick={unfollowClick}
          variant="contained"
          color="secondary"
          size="large"
          type="submit"
        >
          Unfollow
        </Button>

    </div>
  );
};




export default withRouter(FollowProfileButton);
