import Link from "next/link";
import renderHTML from "react-render-html";
import { useState, useEffect } from "react";
import { listSearch } from "../../actions/blog";
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import InputBase from '@material-ui/core/InputBase';
import Divider from '@material-ui/core/Divider';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import SearchIcon from '@material-ui/icons/Search';

const Search = () => {
  const [values, setValues] = useState({
    search: undefined,
    results: [],
    searched: false,
    message: ""
  });

  const { search, results, searched, message } = values;

  const searchSubmit = e => {
    e.preventDefault();
    listSearch({ search }).then(data => {
      setValues({
        ...values,
        searched: true,
        results: data,
        message: `${data.length} blogs found`
      });
    });
  };

  const handleChange = e => {
    setValues({
      ...values,
      search: e.target.value,
      searched: false,
      results: []
    });
  };

  const searchedBlogs = (results = []) => {
    return (
      <div className="jumbotron bg-white">
        {message && <p className="pt-4 text-muted font-italic">{message}</p>}
        {results.map((blog, i) => {
          return (
            <div key={i}>
              <Link href={`/blogs/${blog.slug}`}>
                <a className="text-primary">{blog.title}</a>
              </Link>
            </div>
          );
        })}
      </div>
    );
  };

  const searchForm = () => (
    <form
      className="row pl-0 pr-0 form-inline"
      onSubmit={searchSubmit}
      id="search-form"
      // style={{ width: "100%" }}
    >
      {/* <div className="col-md-10 col-sm-2"> */}
      <Paper component="form" onSubmit={searchSubmit}>
      <IconButton  aria-label="menu">
        <MenuIcon />
      </IconButton>
      <InputBase

        placeholder="Search"
        onChange={handleChange}
      />
      <IconButton type="submit"  aria-label="search">
        <SearchIcon />
      </IconButton>

    </Paper>
      <input
        type="search"
        className="form-control mr-1"
        placeholder="SearchBlogs"
        onChange={handleChange}
      />
      {/* </div> */}
      {/* <div className="col-md-2 col-sm-2"> */}
      <button type="submit">
        <i className="fa fa-search" id="search-button"></i>
      </button>
      {/* </div> */}
      <style jsx>{`
        .form-inline {
          display: flex;
          flex-flow: row wrap;
          align-items: center;
        }
        @media (max-width: 800px) {
          .form-inline input {
            margin: 10px 0;
          }

          .form-inline {
            flex-direction: column;
            align-items: stretch;
          }
        }

      `}</style>
    </form>
  );

  return (
    <div className="container">
      <div className="pb-5">{searchForm()}</div>
      {searched && (
        <div style={{ marginTop: "-100px", marginBottom: "-80px" }}>
          {searchedBlogs(results)}
        </div>
      )}
    </div>
  );
};

export default Search;
