import Private from "../../components/auth/Private";
import Layout from "../../components/Layout";
import Link from "next/link";
import ReadBlogs from '../../components/crud/ReadBlogs'
import {isAuth} from '../../actions/auth'
import { API } from '../../config';
import {useEffect} from 'react'

const UserIndex = () => {
  const username = isAuth() && isAuth().username

  const init = () => {
    getProfile(token).then(data => {
        if (data.error) {
            setValues({ ...values, error: data.error });
        } else {
            setValues({
                ...values,
                usernameImgSrc: data.username,
                name: data.name,
                email: data.email,
                about: data.about
            });
        }
    });
};

useEffect(() => {
    init();
}, []);



  return (
    <Layout>
      <Private>
        <div className="container-fluid">
          <div className="row">
            <div className="col-md-12 pt-5 pb-5">
              <h2>Welcome back to {`${username}`}'s Dashboard</h2>
              <img
                            src={`${API}/user/photo/${username}`}
                            className="img img-fluid img-thumbnail mb-3"
                            style={{ maxHeight: 'auto', maxWidth: '100%' }}
                            alt="user profile"
                        />
            </div>
            <div className="col-md-4">
              <ul className="list-group">
                <li className="list-group-item">
                  <a href="/user/crud/blog">Create Blog</a>
                </li>

                {/* <li className="list-group-item">
                  <Link href="/user/crud/blogs">
                    <a>My Blogs</a>
                  </Link>
                </li> */}

                <li className="list-group-item">
                  <a href="/user/update"> Update Profile</a>
                </li>
              </ul>
            </div>
            {/* <div className="col-md-8">right</div> */}
          </div>
          <ReadBlogs username={username}/>
        </div>
      </Private>
    </Layout>
  );
};



