import Link from "next/link";
import renderHTML from "react-render-html";
import moment from "moment";
import { API } from "../../config";
import ReactImageFallback from "react-image-fallback";
import { Card, CardTitle, CardText, CardSubtitle, CardBody } from "reactstrap";
import LocalOfferIcon from '@material-ui/icons/LocalOffer';
import Chip from '@material-ui/core/Chip';

const BlogCard4 = ({ blog }) => {
    const showBlogCategories = blog =>
      blog.categories.map((c, i) => (
        <Chip key={i} color="secondary" size="medium" icon={<LocalOfferIcon />} label={c.name}>
        {c.name}
      </Chip>
      ));


const bigCard ={
    height:'auto',
    width: '100%',
    boxShadow: '0 14px 28px rgba(0, 0, 0, 0.25)'
}

  return (
    <Card className="big-card mt-3" style={bigCard}>
      <ReactImageFallback
        className="img img-fluid"
        style={{ height: '30vh'}}
        fallbackImage="/static/default.svg"
        src={`${API}/blog/photo/${blog.slug}`}
        alt={blog.title}
      />
      <CardBody>
        <CardTitle>
          <Link href={`/blogs/${blog.slug}`}>
            <a>
              <h2 className="pt-2 pb-2 font-weight-bold">{blog.title}</h2>
            </a>
          </Link>
          {showBlogCategories(blog)}
        </CardTitle>
        <CardSubtitle>
          <span className="text-info">
            Published {moment(blog.updatedAt).fromNow()} by
          </span>

          <span className="text-info">
            <Link href={`/profile/${blog.postedBy.username}`}>
              <a> {blog.postedBy.username}</a>
            </Link>
          </span>
        </CardSubtitle>
        <CardText>{renderHTML(blog.excerpt)}</CardText>
        <Link href={`/blogs/${blog.slug}`}>
          <a className="float-right " style={{padding:'0', borderBottom:'2px solid' ,color:'#171717' }}>
            Read More...
          </a>
        </Link>
      </CardBody>

    </Card>

  );
};

export default BlogCard4;
